// components/visit_list.js
import React, { Component } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';

export default class Visit extends Component {

	constructor(props) {
		super(props)
		this.state = {                // variable estado de la clase, lista de películas
			visit: null
		}
	}


	componentDidMount() {
		fetch('http://localhost:8000/visitas_granada/api/visitas/63/')  // o el que sea
			.then(res => { return res.json()})
			.then(data => {
			this.setState({visit:data})
		}).catch(error => {
			console.log(error)
		})
	}


    render() {
		const visit = this.state.visit ? (
			<>
				<Card style={{ width: '60rem', margin: '50px 0px 00px 60px'}}>
				<CardActionArea>
					<CardMedia
						component="img"
						alt="Contemplative Reptile"
						height="300"
						image={this.state.visit.foto}
						title="Contemplative Reptile"
					/>
					<CardContent>
						<Typography gutterBottom variant="h5" component="h2">
							{this.state.visit.nombre}
						</Typography>
						<Typography variant="body2" color="textSecondary" component="p">
							<b>Descripción:</b> {this.state.visit.descripcion}
						</Typography>
						<Typography variant="body2" color="textSecondary" component="p">
							<b>Likes:</b> {this.state.visit.likes}
						</Typography>
					</CardContent>
				</CardActionArea>
				</Card>
            </>
		) : (
			<>
				<Card style={{ width: '30rem', margin: '50px 0px 00px 60px'}}>
				<CardActionArea>
					<CardContent>
						<Typography variant="body2" color="textSecondary" component="p">
							Cargando visita...
						</Typography>
					</CardContent>
				</CardActionArea>
				</Card>
			</>
		)

        return (
	        <div className ="container">
	            {visit}
	        </div>
        );
    }
}
