// components/visit_list.js
import React, { Component } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
//import Peli from './Peli'

export default class Visit extends Component {

	state = {
		visit: []
	}

	componentDidMount(){
		let visit_id = this.props.match.params.pk;

		fetch('http://localhost:8000/visitas_granada/api/visitas/63/')  // o el que sea
			.then(res => { return res.json()})
			.then(data => {
			console.log(data)
			this.setState({visit: data})
		}).catch(error => {
			console.log(error)
		})
	}

    render() {

		console.log(this.state.visit);
        return (
            <>
				<Card style={{ width: '30rem', margin: '50px 0px 00px 60px'}}>
				<CardActionArea>
					<CardMedia
						component="img"
						alt="Contemplative Reptile"
						height="200"
						image={this.state.visit.foto}
						title="Contemplative Reptile"
					/>
					<CardContent>
						<Typography gutterBottom variant="h5" component="h2">
							{this.state.visit.nombre}
						</Typography>
						<Typography variant="body2" color="textSecondary" component="p">
							<b>Descripci√≥n:</b> {this.state.visit.descripcion}
						</Typography>
						<Typography variant="body2" color="textSecondary" component="p">
							<b>Likes:</b> {this.state.visit.likes}
						</Typography>
					</CardContent>
				</CardActionArea>
				</Card>
            </>
        );
    }
}
