import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

const list = [
    {
        "descripcion": "Visita la Alcazaba de la Alhambra en una visita guiada.",
        "foto": "http://localhost:8000/media/fotos/alcazaba_w5Xbyrf.jpg",
        "likes": 8,
        "nombre": "Alcazaba"
    },
    {
        "descripcion": "Entrada gratuita al Palacio de Comares, aprovecha esta gran oportunidad.",
        "foto": "http://localhost:8000/media/fotos/alhambra_V5aF1Rg.jpg",
        "likes": 11,
        "nombre": "Palacio de Comares"
    },
    {
        "descripcion": "Descubre todos los secretos del Palacio de Carlos V en una visita guiada.",
        "foto": "http://localhost:8000/media/fotos/palacio_carlos_v_42RtsTq.jpg",
        "likes": 0,
        "nombre": "Palacio de Carlos V"
    },
    {
        "descripcion": "¿Alguna vez has entrado a ver el lugar más famoso de la Alhambra?",
        "foto": "http://localhost:8000/media/fotos/patio-leones_p1kGC51.jpg",
        "likes": 0,
        "nombre": "Patio de los Leones"
    },
    {
        "descripcion": "Visita al lugar más tranquilo y relajante de toda la Alhambra.",
        "foto": "http://localhost:8000/media/fotos/granada-generalife_uiurR52.jpg",
        "likes": 2,
        "nombre": "Generalife"
    },
    {
        "descripcion": "Descubre la belleza de la Alhambra iluminada en la oscuridad de la noche.",
        "foto": "http://localhost:8000/media/fotos/nazaries-noche_BNhejL9.jpg",
        "likes": 26,
        "nombre": "Alhambra Nocturna"
    }
];

class App extends Component {
  state = {
    todos: []
  };

  async componentDidMount() {
    try {
      const res = await fetch('http://localhost:8000/visitas_granada/api/visitas/');
      const todos = await res.json();
      this.setState({
        todos
      });
    } catch (e) {
      console.log(e);
    }
  }

  render() {
    return (
      <div>
        {this.state.list.map(item => (
          <div>
            <h1>{item.nombre}</h1>
            <span>Descripción: {item.descripcion}</span>
            <span>Likes: {item.likes}</span>
            <span>{item.foto}</span>
            <img src={item.foto} alt="{item.foto}" height="300"/>
          </div>
        ))}
      </div>
    );
  }
}

export default App;
